INSERT INTO `sto_banners` VALUES (2,'agzs','Газовые заправки','Газовые заправки Киева\r\nВас интересует расположение газовых заправок в Киеве, отзывы о качестве газа на автозаправках, где не доливают, а где пускают воздух? Про все это Вы можете узнать прямо сейчас, сэкономив кучу времени на поисках в интернете! Автомобильный справочник СТО ESTEO с радостью поможет Вам избежать необходимости посещать десятки автомобильных форумов. У Вас есть возможность найти отзывы про любую газовую заправку, не вставая с места, а наша карта газовых заправок Киева покажет все АГЗС в ближайшем радиусе от заданного места.\r\nВне зависимости от Вашего местоположения, Вы сможете найти найти ближайшую качественную газовую заправку, что, согласитесь, очень удобно. \r\nИ все это на одном сайте. Оставьте и Вы отзыв о газовой заправке - поделитесь своим мнением.',1,0,0,0,'40','all','','',1);
